#Sort une répartition des handles par taille
def taille(array)
  lengthmax = array.max {|a, b| a.length <=> b.length}
  max = lengthmax.length
  lengthmin = array.min {|a, b| a.length <=> b.length}
  min = lengthmin.length
  rendu=[]
  for n in min..max
    stockage = [array.select{|e| e.length==n}]
    if stockage.count > 0
      puts "Voici les handles dont la longueur est de #{n} caractères :"
      puts stockage
    end
  end
end

def perform(journalistes)
  puts "Bonjour ! Nous avons crée une liste d'array, ainsi qu'un programme répondant à une liste définie de questions."
  puts "Il vous suffit de sélectionner une question afin d'en avoir la réponse !"
  puts "Appuyer sur Entrée pour passer à la liste des questions"
  gets.chomp
  puts "-- Liste de Questions --"
  puts "0. Combien y a-t-il de handle dans cet array?"
  puts "1. Quel est le handle le plus court de la liste?"
  puts "2. Combien y a-t-il de handles contenant 5 caractères (le @ n'étant pas considéré comme un caractère)?"
  puts "3. Combien commencent par une majuscule (première lettre juste après le @) ?"
  puts "4. Trie la liste de handle par ordre alphabétique?"
  puts "5. Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)"
  puts "6. Quelle est la position dans l'array de la personne @epenser ?"
  puts "7. Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)"
  puts "-- Fin des Questions --"

  input = gets.chomp
  if input == "0"
    #On cherche la taille de l'array. On aurait également pu utiliser le .count
    puts "L'array de journalistes contient #{journalistes.length} Handles."
  elsif input == "1"
    #Récupéré sur la documentation Ruby : Soit a et b les items 0 et 1 de l'array; la fonction compare (<=>) la taille (.length) et en ressort la plus petite (array.min)
    puts "Le journaliste ayant l'handle le plus court est #{journalistes.min {|a, b| a.length <=> b.length}}."
  elsif input == "2"
    a=journalistes.select{|e| e.length==6}
    puts "Le nombre de handles qui sont composés de 5 caractères est de #{a.length}."
  elsif input == "3"
    b=journalistes.select{|e| e[1] =~ /[A-Z]/}
    puts "Le nombre de handles qui commencent par une majuscule est de #{b.count}."
  elsif input == "4"
    puts "#{journalistes.sort_by(&:downcase) }"
  elsif input == "5"
    puts "#{journalistes.sort_by{|e| e.length}}"
  elsif input == "6"
    puts "La position de @epenser est #{journalistes.index('@epenser')}."
  elsif input == "7"
    number7 = taille(journalistes)
  else
    puts "Je n'ai pas compris..."
  end
end

perform(journalistes)





